appsmith.onModelChange(() => {
  const tableData = appsmith.model; // Assuming the data is available in appsmith.model

  // Convert tableData from object to array
  const dataArray = Object.values(tableData);

  // If there are no items, return early
  if (dataArray.length === 0) return;

  // Get headers from the first item
  let headers = Object.keys(dataArray[0]);

  // Remove 'tenantId' from headers if it exists
  headers = headers.filter(header => header !== 'tenantId');

  // Add new column header for index
  headers.unshift('#');

  // Add checkbox header for selection
  headers.unshift(' ');

  const tableBody = document.getElementById('tableBody');
  const tableHeader = document.getElementById('tableHeader');

  // Ensure elements are available
  if (!tableBody || !tableHeader) {
    console.error('Table body or header element not found.');
    return;
  }

  let selectedRows = []; // Use an array to manage selected rows

  function renderTable(headers, tableData) {
    tableHeader.innerHTML = '';
    tableBody.innerHTML = '';

    // Create header row
    const headerRow = document.createElement('tr');
    headers.forEach((header, index) => {
      const th = document.createElement('th');
      th.textContent = header.charAt(0).toUpperCase() + header.slice(1);
      th.draggable = true;
      th.dataset.index = index;

      if (header === '') {
        th.textContent = '';
        const checkbox = document.createElement('input');
        checkbox.type = 'checkbox';
        th.appendChild(checkbox);
      }

      // Add resize handle
      const resizeHandle = document.createElement('div');
      resizeHandle.className = 'resize-handle';
      th.appendChild(resizeHandle);

      // Resizing functionality
      resizeHandle.addEventListener('mousedown', (e) => {
        e.stopPropagation(); // Prevent drag start
        const startX = e.clientX;
        const startWidth = th.offsetWidth;

        const onMouseMove = (e) => {
          th.style.width = `${startWidth + e.clientX - startX}px`;
        };

        const onMouseUp = () => {
          document.removeEventListener('mousemove', onMouseMove);
          document.removeEventListener('mouseup', onMouseUp);
        };

        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      });

      // Drag-and-drop functionality
      th.addEventListener('dragstart', (e) => {
        e.dataTransfer.setData('text/plain', e.target.dataset.index);
        e.target.classList.add('dragging');
      });

      th.addEventListener('dragend', (e) => {
        e.target.classList.remove('dragging');
      });

      th.addEventListener('dragover', (e) => {
        e.preventDefault();
      });

      th.addEventListener('drop', (e) => {
        e.preventDefault();
        const fromIndex = e.dataTransfer.getData('text/plain');
        const toIndex = e.target.dataset.index;

        if (fromIndex !== toIndex) {
          // Update headers
          const movedHeader = headers.splice(fromIndex, 1)[0];
          headers.splice(toIndex, 0, movedHeader);

          // Re-render table with new header order
          renderTable(headers, dataArray);
        }
      });

      headerRow.appendChild(th);
    });

    tableHeader.appendChild(headerRow);

    // Create data rows
    dataArray.forEach((item, rowIndex) => {
      const tr = document.createElement('tr');

      // Add checkbox for selection
      const checkboxTd = document.createElement('td');
      const checkbox = document.createElement('input');
      checkbox.type = 'checkbox';
      checkbox.addEventListener('change', () => {
        const rowValues = headers.slice(2).reduce((acc, header, index) => {
          acc[header] = tr.cells[index + 2].textContent;
          return acc;
        }, {});

        if (checkbox.checked) {
          if (!selectedRows.some(row => JSON.stringify(row) === JSON.stringify(rowValues))) {
            selectedRows.push(rowValues);
          }
        } else {
          selectedRows = selectedRows.filter(row => JSON.stringify(row) !== JSON.stringify(rowValues));
        }

        // Here you can store the selectedRows in a variable or pass it to a function
        console.log('Selected rows:', selectedRows);
				const val={selectedRows};
        appsmith.updateModel(val);
      });
      checkboxTd.appendChild(checkbox);
      tr.appendChild(checkboxTd);

      // Add index column
      const indexTd = document.createElement('td');
      indexTd.textContent = rowIndex + 1; // Index starts from 1
      tr.appendChild(indexTd);

      headers.slice(2).forEach(header => { // Skip the first two headers (Select and Index)
        const td = document.createElement('td');
        td.textContent = item[header] || '';
        tr.appendChild(td);
      });

      tableBody.appendChild(tr);
    });
  }

  // Initial render
  renderTable(headers, dataArray);
});

