appsmith.onReady(() => {
  // JSON object with options and suboptions
  var data = appsmith.model;

  // Function to create the accordion menu
  function createAccordionMenu(data) {
    var accordionContainer = document.getElementById("accordion-container");

    // Store the currently selected page
    let selectedPage = data.selectedPage;
    console.log(selectedPage);
    data.items.options.forEach(function (option) {
      var mainOption = document.createElement("button");
      mainOption.classList.add("accordion");

      // Check if subOptions are present
      if (option.subOptions && option.subOptions.length > 0) {
        mainOption.innerHTML = '<i class="fi fi-' + option.icon + '"></i>' + option.mainOption + '<i class="fi fi-rr-caret-down arrow"></i>';
      } else {
        mainOption.classList.add("no-suboptions");
        mainOption.innerHTML = '<i class="fi fi-' + option.icon + '"></i>' + option.mainOption;
      }

      // Add a data-id attribute to uniquely identify the mainOption
      mainOption.setAttribute("data-id", option.mainOption);

      // If this option is selected, add active class
      if (selectedPage === option.mainOption) {
        mainOption.classList.add("active");
      }
      accordionContainer.appendChild(mainOption);
      var panel = document.createElement("div");
      panel.classList.add("panel");

      // Add sub-options if they exist
      if (option.subOptions) {
        accordionContainer.appendChild(panel);
        option.subOptions.forEach(function (subOption, index) {
          var subOptionElement = document.createElement("div");
          subOptionElement.classList.add("sub-option");
          subOptionElement.setAttribute("data-id", option.mainOption + "-" + subOption.item);
          subOptionElement.innerHTML = '<i class="fi fi-' + subOption.icon + '"></i>' + subOption.item;
          panel.appendChild(subOptionElement);

          // If this subOption is selected, add active class
          if (selectedPage === subOption.item) {
            subOptionElement.classList.add("active");
            mainOption.classList.add("active");
            panel.style.display = "block";
          }
          subOptionElement.addEventListener("click", function (event) {
            // Remove active class from all sub-options
            accordionContainer.querySelectorAll('.sub-option').forEach(function (el) {
              el.classList.remove('active');
            });
            // Add active class to the clicked sub-option
            subOptionElement.classList.add('active');
            // Update the selectedPage to the current sub-option
            selectedPage = subOption.item;
            // Add the Appsmith event to send the information needed
            appsmith.triggerEvent("onClick", {
              itemId: index,
              type: subOption.type,
              navTo: subOption.navTo
            });
            event.preventDefault();
          });
        });
      } else {
        // If no sub-options, trigger navigation
        mainOption.addEventListener("click", function () {
          selectedPage = option.mainOption;
          appsmith.triggerEvent("onClick", {
            itemId: null,
            type: option.type,
            navTo: option.navTo
          });
        });
      }
      mainOption.addEventListener("click", function () {
        if (option.subOptions) {
          panel.style.display = panel.style.display === "block" ? "none" : "block";
          mainOption.classList.toggle("active");
        }
      });
    });

    // Highlight the selected option when reloading
  }

  // Create the accordion menu
  createAccordionMenu(data);
});

