function initApp() {
  var uploadButton = document.getElementById('upload-button');
  var fileInput = document.getElementById('file-upload');
  var fileNameDisplay = document.getElementById('file-name');
  var uploadDocButton = document.getElementById('upload-doc');
  var cancelIcon = document.getElementById('cancel-icon');
  uploadButton.onclick = function () {
    fileInput.click(); // Trigger the file input dialog
  };
  fileInput.onchange = function () {
    if (this.files && this.files[0]) {
      var file = this.files[0];
      var allowedExtensions = /(\.xls|\.xlsx)$/i;
      if (!allowedExtensions.exec(file.name)) {
        alert('Please upload an Excel file (.xls or .xlsx).');
        fileInput.value = ''; // Clear the file input
        fileNameDisplay.textContent = 'Upload Excel';
        cancelIcon.style.display = 'none';
        return;
      }
      fileNameDisplay.textContent = file.name;
      cancelIcon.style.display = 'inline-block';
    } else {
      fileNameDisplay.textContent = 'Upload Excel';
      cancelIcon.style.display = 'none';
    }
  };
  cancelIcon.onclick = function () {
    fileInput.value = ''; // Clear the file input
    fileNameDisplay.textContent = 'Upload Excel';
    cancelIcon.style.display = 'none';
  };
  uploadDocButton.onclick = function () {
    if (fileInput.files && fileInput.files[0]) {
      var file = fileInput.files[0];

      // Log file name or metadata
      console.log('File selected:', file.name);

      // Clear the file input
      fileInput.value = '';
      fileNameDisplay.textContent = 'No file chosen';
      cancelIcon.style.display = 'none';

      // If you need to handle the file, read its content
      var reader = new FileReader();
      reader.onload = function (event) {
        var fileContent = event.target.result;

        // Example: send file content to a server or process it
        console.log('File content:', fileContent);

        // Example of using Appsmith to update the model
        var fileData = {
          name: file.name,
          size: file.size,
          type: file.type,
          content: fileContent // or use the value as needed
        };
        appsmith.updateModel({
          fileData
        });
        appsmith.triggerEvent("onClick");
      };
      reader.readAsText(file); // Read as text or use other methods as needed
    }
  };
}
appsmith.onReady(() => {
  initApp();
});

