.accordion-container {
  position: fixed;
  width: 250px; /* Default width */
  transition: width 0.3s; /* Smooth transition */
}

.accordion-container.collapsed {
  width: 80px; /* Collapsed width */
}

.accordion {
  background-color: #FFFFFF;
  color: #71717a;
  cursor: pointer;
  padding: 10px;
  width: 100%;
  border: none;
  text-align: left;
  outline: none;
  font-size: 16px;
  transition: 0.4s;
  font-family: Nunito Sans;
  display: flex;
  align-items: center; /* Ensure items are vertically centered */
}

.accordion i {
  vertical-align: middle; /* Icon vertical position */
  margin-right: 20px; /* Margin with text */
}

.accordion .main-option-text {
  flex-grow: 1;
}

.accordion.active, .accordion:hover {
  color: #007DC5;
  background-color: #dbeafe;
}

.panel {
  padding: 0 18px;
  display: none;
  background-color: #FFFFFF;
  overflow: hidden;
}

.sub-option {
  padding: 10px;
  cursor: pointer;
  font-family: Nunito Sans;
  font-size: 16px;
  color: #015083;
  border-left: 2px solid #d4d4d8;
}

.sub-option i {
  vertical-align: text-bottom; /* Icon vertical position */
  margin-right: 20px; /* Margin with text */
}

.sub-option:hover {
  color: #007DC5;
}

.sub-option.active {
  border-left: 2px solid blue;
  font-weight: bold; /* Highlighted border color */
}

.accordion.selected-page {
  background-color: #f0f0f0; /* Highlight selected page */
}

.arrow {
  margin: auto;
}

.accordion-container.collapsed .accordion {
  text-align: center; /* Center text in collapsed mode */
}

.accordion-container.collapsed .accordion i {
  margin-right: 0; /* Remove margin when collapsed */
}

.accordion-container.collapsed .main-option-text,
.accordion-container.collapsed .arrow,
.accordion-container.collapsed .panel,
.accordion-container.collapsed .sub-option {
  display: none; /* Hide elements in collapsed mode */
}

.toggle-button {
  position: fixed;
  top: 10px;
  right: 10px;
  background-color: #007DC5;
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
  border-radius: 5px;
}

.toggle-button i {
  vertical-align: middle;
}

