.app {
  margin: 20px;
}

.tip-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: relative;
}

.left-section {
  display: flex;
  align-items: center;
}

.title-subtitle-container {
  display: flex;
  align-items: center;
}

#title {
  margin-right: 10px;
  color: #3b82f6;
}

.vertical-line {
  width: 1px;
  height: 30px; /* Adjust based on the height of the search input */
  background-color: #ccc;
  margin: 0 10px; /* Space around the vertical line */
}

#subtitle {
  margin-left: 10px;
  color: #3b82f6;
}

.search-container {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5px;
  border: 1px solid #ccc;
  border-radius: 4px;
  max-width: 300px; /* Adjust as needed */
}

.search-input {
  flex: 1;
  padding: 10px;
  border: none;
  outline: none;
  box-sizing: border-box;
}

.search-icon {
  font-size: 24px;
  color: #000;
  margin-right: 5px;
}

.right-section {
  display: flex;
  align-items: center;
  justify-content: flex-start; /* Ensure contents are aligned to the start of the section */
}

.notification-layout {
  margin-right: 15px;
}

.user-info {
  display: flex;
  align-items: center;
}

.profile-circle {
  width: 40px; /* Adjust size as needed */
  height: 40px;
  border-radius: 50%;
  background-color: #ccc;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 10px;
  font-size: 18px;
  color: #fff;
  font-weight: bold;
}

#username-initial {
  /* Adjust font size, color, and other styles as needed */
}

#name, #email {
  margin: 0;
}

