export default {
    async downloadFile() {
        try {
            // Run the API to get the file data
            var res = await Download_Unavailable_API.run();

            // Log the result for debugging
            console.log("Download file complete");
            console.log(res);

            // Convert the byte array to a binary string
            const byteArray = new Uint8Array(res.fileData); // Assuming res.fileData contains the byte array
            let binaryString = "";
            for (let i = 0; i < byteArray.length; i++) {
                binaryString += String.fromCharCode(byteArray[i]);
            }
            const base64String = btoa(binaryString);

            // Set MIME type for Excel files and filename with appropriate extension
            const mimeType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"; // For .xlsx files
